import React, { useState } from "react";
import Card from "../UI/Card";

import classes from "./AddUser.module.css";
import Button from "../UI/Button";
import ErrorModel from "../UI/ErrorModel";

const AddUser = (props) => {
  const [enteredUserName, setUserName] = useState("");
  const [enteredAge, setAge] = useState("");
  const [error, setError] = useState("");

  const addUserHandler = (event) => {
    event.preventDefault();

    if (enteredUserName.trim().length === 0 || +enteredAge.trim().length === 0) {
      setError({
        title: 'Invalid input',
        message: 'Please enter a valid name and age (non-empty values).',
      });
      return;
    }

    if (+enteredAge < 0) {
      setError({
        title: 'Invalid age',
        message: 'Please enter a valid age (> 0).',
      });
      return;
    }

    props.onAddUser(enteredUserName, enteredAge);
    setEnteredUsername('');
    setEnteredAge('');
  };

  const setEnteredUsername = (event) => {
    setUserName(event.target.value);
  };
  const setEnteredAge = (event) => {
    setAge(+event.target.value);
  };

  const errorModalHandler = () => {
      setError(null)
  }

  return (
    <div>
      <ErrorModel title={"Error"} message={"hiiii"} onConfirm = {errorModalHandler}/>
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label>Username</label>
          <input type="text" onChange={setEnteredUsername}></input>
          <label>Age (Years)</label>
          <input type="number" onChange={setEnteredAge}></input>
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </div>
  );
};

export default AddUser;
