import React, { Component } from "react";

export class ErrorBoundary extends Component {
  constructor() {
    super(_)
    this.state = {
      isError: false,
    };
  }
  componentDidCatch() {
    this.setState({ isError: true });
  }

  render() {
    if (!isError) {
      return <p>There is Something Wrong</p>;
    }
    return this.props.children;
  }
}

export default ErrorBoundary;
