import { createSlice } from "@reduxjs/toolkit";

const initialUiState = {
    isOpen: false,
}

const uiSlice = createSlice({
    name: 'UI',
    initialState:initialUiState,
    reducers: {

        openCart: (state) => {
            state.isOpen = true;
        },

        closeCart: (state) => {
            state.isOpen = false;
        },

    }
});


export const uiReducer = uiSlice.actions;
export default uiSlice;
