import { useSelector } from 'react-redux';
import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';

const Cart = (props) => {

  const {id , title, quantity, price, } = useSelector((state) => state.cart.items)

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        <CartItem
        key = {id}
          item={{ title: title, quantity: quantity, total: 18, price: price }}
        />
      </ul>
    </Card>
  );
};

export default Cart;
